mapper.select:
SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 1

    AND n_title LIKE CONCAT('%', #{n_title}, '%')

ORDER BY n_idx DESC
) a, (SELECT @RN:=0) b
) c 
WHERE c.rnum between #{begin} AND #{end};

SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 1

    AND n_title LIKE CONCAT('%', #{n_title}, '%')


        AND n_idx = #{n_idx}

ORDER BY n_idx DESC
) a, (SELECT @RN:=0) b
) c 

WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 4

    AND n_title LIKE CONCAT('%', #{n_title}, '%')

ORDER BY n_idx DESC
) a, (SELECT @RN:=0) b
) c WHERE c.rnum between #{begin} AND #{end};

SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 4

    AND n_title LIKE CONCAT('%', #{n_title}, '%')
    AND n_idx = #{n_idx}

ORDER BY n_idx DESC
) a, (SELECT @RN:=0) b
) c WHERE c.rnum between #{begin} AND #{end};

SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 2
AND n_idx = #{n_idx}

SELECT * FROM notice
WHERE n_status = 0 AND nt_ct_code = 3
AND n_idx = #{n_idx}

select * from notice
nt_ct_code = 1
n_none = #{n_none}

mapper.delete:

	DELETE FROM notice
    WHERE n_idx = #{n_idx}
    AND nt_ct_code = 1

mapper.select:
select * from notice
nt_ct_code = 4 and n_status = 0
n_none = #{n_none}

mapper.delete:

	DELETE FROM notice
    WHERE n_idx = #{n_idx}
    AND nt_ct_code = 2

mapper.select:
select * from notice
(nt_ct_code = 3 OR nt_ct_code = 4)
n_none = #{n_none}
